$(document).ready(function(){

    $("#1_name").keyup(function() {
        $("#gbox_jqGrid").css('display', 'block');
		var str="<button type='button' class='down down1' onClick=show1()></button>";
		$("#1_name").append(str);
   
	
})
})
function show1() {
	$(this).click(function(){
		if($("button.down").hasClass("down1")){
		$("#gbox_jqGrid").css('display', 'block');
		$("button.down").removeClass("down1");
		}
		else{
			$("#gbox_jqGrid").css('display', 'block');
			$("button.down").addClass("down1");
			}
		
		});
   
}

